import {useState,useEffect} from 'react';
import styled from 'styled-components';
import Delete from '../Buttons/Delete';
import Edit from '../Buttons/Edit';
import Copy from '../Buttons/Copy';
import {connect} from 'react-redux';
import {Deleteitem,CopyItem,Fetchdata} from '../../../Actions'
import { useDispatch , useSelector } from 'react-redux'
import { additemInitiate, getitemsInitiate, deleteitemInitiate, edititemInitiate } from '../../../Actions/action';


const TableBody = (props) =>{

    const {data} = useSelector(state=>state.data)
    const [editMode,seteditMode] = useState(false)
    const dispatch = useDispatch()

   const [input,setinput] = useState({
       code:'',
       domain: '',
       cpanel : '',
       username: '',
       password : '',
       owner: ''
   })

    const onchangehandler = (event) =>{
        setinput({...input,[event.target.id]:event.target.value})
    }
    const submitHandler = (e) =>{   
        e.preventDefault()
        dispatch(additemInitiate(input))
        setinput({code:'',domain: '',cpanel : '',username: '',password : '',owner: ''})
    }
    const deletehandler = (id) =>{
        if(window.confirm('are you sure?')){
            dispatch(deleteitemInitiate(id))
        }
    }
    const edithandler = (id) =>{
        seteditMode(true)
        dispatch(edititemInitiate(id))
    }
    useEffect(()=>{
        dispatch(getitemsInitiate())
    },[])

    return(

        <>
            {
               data && data.map((item)=>{
                    return(
                        <Head key={item.id}>
                            {/* <div style={{width:'2.5%'}}>{item.id}</div> */}
                            <div style={{width:'7.5%'}}>{item.code}</div>
                            <div style={{width:'20%'}}>{item.domain}</div>
                            <div style={{width:'20%'}}>{item.cpanel}</div>
                            <div style={{width:'12.5%'}}>{item.username}</div>
                            <div style={{width:'12.5%'}}>{item.password}</div>
                            <div style={{width:'10%'}}>{item.owner}</div>
                            {/* <Delete Deletehandler={Deletehandler} id = {item.id}/> */}
                            <button onClick={()=>deletehandler(item.id)}>Del</button>
                            {/* <Edit/> */}
                            <button onClick={()=>edithandler(item.id)}>Edit</button>
                            {/* <Copy  Copyhandler={Copyhandler} id = {item.id}/> */}
                        </Head>
                    )
                })
            }

            <form onSubmit={submitHandler}>
                <label>Code</label>
                <input name = 'code' id='code' onChange={onchangehandler} />
                <label>Domain</label>
                <input name = 'domain' id='domain' onChange={onchangehandler}/>
                <label>Cpanel</label>
                <input name = 'cpanel' id='cpanel' onChange={onchangehandler} />
                <label>Username</label>
                <input name = 'username' id='username' onChange={onchangehandler}/>
                <label>Password</label>
                <input name = 'password' id='password' onChange={onchangehandler}/>
                <label>Owner</label>
                <input name = 'owner' id='owner' onChange={onchangehandler}/>
                <button type='submit'>Send</button>
            </form>
        </>
    )
}
const Head = styled.div`
    display:flex;
    justify-content:space-between;
    color:white;
    margin-bottom:10px;
    div{
        text-align:center;
    }
`
const mapStateprops = state =>{
    return{
        items:state.items
    }

}
// ---- same with inside currly brackets
// const mapDispatchToProps = (dispatch) =>{
//     return {
//         Fetchdata: (item) => dispatch(Fetchdata(item))
//     }
// }
export default connect(mapStateprops,{Deleteitem,CopyItem,Fetchdata})(TableBody)
