import {useState,useEffect} from 'react';
import styled from 'styled-components';
import Delete from '../Buttons/Delete';
import Edit from '../Buttons/Edit';
import Copy from '../Buttons/Copy';
import {connect} from 'react-redux';
import {Deleteitem,CopyItem,Fetchdata} from '../../../Actions'
import { useDispatch , useSelector } from 'react-redux'
import { additemInitiate, getitemsInitiate, deleteitemInitiate, edititemInitiate,updateitemInitiate,deleteitemAllInitiate, copyitemInitiate } from '../../../Actions/action';


const TableBody = ({search}) =>{

    const {data,website,searchdata} = useSelector(state=>state.data)

    // const searcheddata = data.filter((i)=> {
    //     return Object.values(i).join(" ").toLowerCase().includes(search.toLowerCase())
    // })
    const [editMode,seteditMode] = useState(false)
    const [websiteid, setwebsiteid] = useState(null)
    const radioarr = []

    const dispatch = useDispatch()
    const initialstate = {
    code:'',
    domain: '',
    cpanel : '',
    username: '',
    password : '',
    owner: ''}
    const [input,setinput] = useState(initialstate)
    const {code,domain,cpanel,username,password,owner} = input || {};

    const onchangehandler = (event) =>{
        setinput({...input,[event.target.id]:event.target.value})
    }
    const submitHandler = (e) =>{   
        e.preventDefault()
        if(!editMode){
            dispatch(additemInitiate(input))
            setinput({code:'',domain: '',cpanel : '',username: '',password : '',owner: ''})
        }else{
            dispatch(updateitemInitiate(websiteid,input))
            setwebsiteid(null);
            seteditMode(false);
            setinput({code:'',domain: '',cpanel : '',username: '',password : '',owner: ''})
        }

    }
    const deletehandler = (id) =>{
        if(window.confirm('are you sure?')){
            dispatch(deleteitemInitiate(id))
        }
    }
    const edithandler = (id) =>{
        seteditMode(true)
        dispatch(edititemInitiate(id))
        setwebsiteid(id)
    }
    const copyhandler = (copieditemdata) =>{
        dispatch(copyitemInitiate(copieditemdata))
    }

    const deleteallhandler = (id) => {
        radioarr +=[id]
        console.log(radioarr)
    }
    const deleteall = (radioarr) =>{
        dispatch(deleteitemAllInitiate(radioarr))
    }
    useEffect(()=>{
        dispatch(getitemsInitiate())
    },[])
    useEffect(()=>{
        if(website){
            setinput({...website})
        }
    },[website])

    return(

        <>
         <button onClick={()=>deleteall(radioarr)}>Del All</button>
            {

               searchdata && searchdata.map((item)=>{
                    return(
                        <Head key={item.id}>
                            <input type="radio" value="" name="" onClick={()=>deleteallhandler(item.id)} />
                            <div style={{width:'7.5%'}}>{item.code}</div>
                            <div style={{width:'20%'}}>{item.domain}</div>
                            <div style={{width:'20%'}}>{item.cpanel}</div>
                            <div style={{width:'12.5%'}}>{item.username}</div>
                            <div style={{width:'12.5%'}}>{item.password}</div>
                            <div style={{width:'10%'}}>{item.owner}</div>
                            <button onClick={()=>deletehandler(item.id)}>Del</button>
                            {/* <Edit/> */}
                            <button onClick={()=>copyhandler(item)}>Copy</button>
                            <button onClick={()=>edithandler(item.id)}>Edit</button>
                            {/* <Copy  Copyhandler={Copyhandler} id = {item.id}/> */}
                        </Head>
                    )
                })
            }

            <form onSubmit={submitHandler}>
                <label>Code</label>
                <input name = 'code' id='code' value={code || ''} onChange={onchangehandler} />
                <label>Domain</label>
                <input name = 'domain' id='domain' value={domain || ''} onChange={onchangehandler}/>
                <label>Cpanel</label>
                <input name = 'cpanel' id='cpanel' value={cpanel || ''} onChange={onchangehandler} />
                <label>Username</label>
                <input name = 'username' id='username' value={username || ''} onChange={onchangehandler}/>
                <label>Password</label>
                <input name = 'password' id='password' value={password || ''} onChange={onchangehandler}/>
                <label>Owner</label>
                <input name = 'owner' id='owner' value={owner || ''} onChange={onchangehandler}/>
                <button type='submit'>Send</button>
            </form>
        </>
    )
}
const Head = styled.div`
    display:flex;
    justify-content:space-between;
    color:white;
    margin-bottom:10px;
    div{
        text-align:center;
    }
`
// const mapStateprops = state =>{
//     return{
//         items:state.items
//     }

// }
// ---- same with inside currly brackets
// const mapDispatchToProps = (dispatch) =>{
//     return {
//         Fetchdata: (item) => dispatch(Fetchdata(item))
//     }
// }
export default connect()(TableBody)
