import {useState,useEffect} from 'react';
import styled from 'styled-components';
import Delete from '../Buttons/Delete';
import Edit from '../Buttons/Edit';
import Copy from '../Buttons/Copy';
import {connect} from 'react-redux';
import {Deleteitem,CopyItem,Fetchdata} from '../../../Actions'
import { useDispatch , useSelector } from 'react-redux'
import { additemInitiate, getitemsInitiate } from '../../../Actions/action';
// import { db } from '../../../firebaseConfig/fbConfig'
// import { collection, getDocs } from 'firebase/firestore'

const TableBody = (props) =>{
    // const websiteCollectionRef = collection(db,'websites')
    const [data,setdata] = useState()
    const dispatch = useDispatch()
    // useEffect(()=>{
    //     const getWebsites = async () =>{
    //         const data = await getDocs(websiteCollectionRef)
    //         setdata(data.docs.map((doc)=>({...doc.data(),id: doc.id})))
    //     }
    //     getWebsites()
    // },[])
    // const state = {data:props.items}


   const [input,setinput] = useState({
       code:'',
       domain: '',
       cpanel : '',
       username: '',
       password : '',
       owner: ''
   })
    const onchangehandler = (event) =>{
        setinput({...input,[event.target.id]:event.target.value})
    }
    const submitHandler = (e) =>{   
        e.preventDefault()
        dispatch(additemInitiate(input))
    }
    useEffect(()=>{
        setdata(dispatch(getitemsInitiate()))
        console.log(data)
    },[])

    return(

        <>
            {
               data && data.map((item)=>{
                    return(
                        <Head key={item.id}>
                            <div style={{width:'2.5%'}}>{item.id}</div>
                            <div style={{width:'7.5%'}}>{item.code}</div>
                            <div style={{width:'20%'}}>{item.domain}</div>
                            <div style={{width:'20%'}}>{item.cpanel}</div>
                            <div style={{width:'12.5%'}}>{item.username}</div>
                            <div style={{width:'12.5%'}}>{item.password}</div>
                            <div style={{width:'10%'}}>{item.owner}</div>
                            {/* <Delete Deletehandler={Deletehandler} id = {item.id}/> */}
                            <button onClick={()=>props.Deleteitem(item.id)}>Click</button>
                            <Edit/>
                            <button onClick={()=>props.CopyItem(item.id)}>Copy</button>
                            {/* <Copy  Copyhandler={Copyhandler} id = {item.id}/> */}
                        </Head>
                    )
                })
            }

            <form onSubmit={submitHandler}>
                <label>Code</label>
                <input name = 'code' id='code' onChange={onchangehandler} />
                <label>Domain</label>
                <input name = 'domain' id='domain' onChange={onchangehandler}/>
                <label>Cpanel</label>
                <input name = 'cpanel' id='cpanel' onChange={onchangehandler} />
                <label>Username</label>
                <input name = 'username' id='username' onChange={onchangehandler}/>
                <label>Password</label>
                <input name = 'password' id='password' onChange={onchangehandler}/>
                <label>Owner</label>
                <input name = 'owner' id='owner' onChange={onchangehandler}/>
                <button>Send</button>
            </form>
        </>
    )
}
const Head = styled.div`
    display:flex;
    justify-content:space-between;
    color:white;
    margin-bottom:10px;
    div{
        text-align:center;
    }
`
const mapStateprops = state =>{
    return{
        items:state.items
    }

}
// ---- same with inside currly brackets
// const mapDispatchToProps = (dispatch) =>{
//     return {
//         Fetchdata: (item) => dispatch(Fetchdata(item))
//     }
// }
export default connect(mapStateprops,{Deleteitem,CopyItem,Fetchdata})(TableBody)
