import * as types from './actionTypes'
import {auth} from '../firebaseConfig/fbConfig'

const loginstart = () => ({
    type:types.LOGINSTART
})
const loginsuccess = () =>({
    type:types.LOGINSUCCESS
})
const loginerror = () => ({
    type:types.LOGINERROR
})

const registerstart = () =>({
    type:types.REGISTERSTART
})
const registersuccess = (user) =>({
    type:types.REGISTERSUCCESS,
    payload:user
})
const registererror = (error) =>({
    type:types.REGISTERERROR,
    payload:error
})
export const registerInitiate = (mail,password,username) => {
    return function(dispatch){
        dispatch(loginstart());
        auth.createUserWithEmailAndPassword(mail,password)
        .then(({user})=>{
            user.updateProfile({
                username
            })
            dispatch()
        })

    }

}
