import {useEffect} from 'react'
import styled from 'styled-components';
import './index.css'
import Main from './components/Main/Main';
import { BrowserRouter,Routes,Route,useNavigate } from "react-router-dom";
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import UserRoute from './components/Auth/UserRoute';
import { useDispatch,useSelector } from 'react-redux';
import { auth } from './firebaseConfig/fbConfig'
import {setuser, userRole} from './Actions/authaction'
import Userlist from './components/Userlists/Userlist';
import db from './firebaseConfig/fbConfig';
import axios from 'axios'
import Scrapped from './Pages/Scrapped';
import Verifyemail from './components/Auth/Verifyemail';

function App() {
  const dispatch = useDispatch()
  function Redirect({ to }) {
    let navigate = useNavigate();
    useEffect(() => {
      navigate(to);
    });
    return null;
  }
  useEffect(()=>{
    auth.onAuthStateChanged((currentUser)=>{
      if(currentUser){
        dispatch(setuser(currentUser))
        db.collection('user').doc(currentUser.uid).get().then((user)=>{
          dispatch(userRole({...user.data()}))
      })
      }else{
        dispatch(setuser(null))
      }
    })
  },[dispatch])
  const {currentuser} = useSelector(state =>state.user)
  let routes;
    if(currentuser && currentuser.emailVerified){
      routes = (
        <Routes>
          <Route exact path='/' element ={<Main Redirect to='/'/>}/>
          <Route path='userlist' element ={<Userlist/>}></Route>
          <Route path='scrapped' element ={<Scrapped/>}></Route>
        </Routes>
      )

    }
    else if(currentuser && !currentuser.emailVerified){
      routes = (
        <Routes>
          <Route exact path='verifyemail' element ={<Verifyemail Redirect to='verifyemail'/>}></Route>
          {/* <Navigate to='verifyemail'/> */}
        </Routes>
      )

    }else{
      routes = (
        <Routes>
          <Route path='login' element={<Login Redirect to='/'/>}/>
          <Route path='register' element={<Register/>}/>
        </Routes>
      )

    }

  return (
    <>
      {routes}
    </>
  );
}



export default App ;
