import {useState} from 'react'
import styled from 'styled-components'
import {useDispatch,useSelector} from 'react-redux'
import { hideElement } from '../../Actions/authaction'

const Setting = () =>{
    const dispatch  = useDispatch()
    const {currentuser,hide} = useSelector(state =>state.user)
    console.log('userinfo',currentuser.displayName,currentuser.email)
    const hideHandler = () =>{
        dispatch(hideElement(false))
    }
    return(
        <>
            {hide && 
            <Main>
                <div onClick={hideHandler}>X</div>
                <h1>Personal Details</h1>
                <form>
                    <label>Username</label>
                    <input type='text' id='username' value={currentuser.displayName}></input>
                    <label>Email</label>
                    <input type='email' id='email' value={currentuser.email}></input>
                    <button type='submit'> Save </button>
                </form>
            </Main>
            }

        </>
    )
}
const Main = styled.div`
    background-color:gray;
    width:40%;
    padding:50px;
    position: absolute;
    margin-left: auto;
    margin-right: auto;
    left: 0;
    right: 0;
    text-align: center;
    z-index:10;
    div{
        right:7%;
        top:10%;
        position: absolute;
        color:white;
        cursor:pointer
    }
    form{
        display:flex;
        flex-direction:column;

    }
    input{
        border:1px solid red;
        height:30px;
        margin-bottom:10px;
    }
    button{
        height:40px;
        width:80px;
        background-color:red;
        margin:auto;
    }
`

export default Setting
