import * as types from '../Actions/actionTypes';

const initialState = {
    data:[]
}
const websiteReducer = (state=initialState,action) => {
    switch (action.type){
        case types.GETITEMS:
            return({data:action.payload})
        case types.EDITITEM:
            return({...state,website:action.payload})
        case types.SEARCHITEMS:
            const {value} = action;
            const data = state.data.filter((val) => [val].includes(value));
            return {data};
            // return(console.log(state.data.filter((val)=> val.include({action}))))
        default:
            return state
    }
}
export default websiteReducer
