import * as types from '../Actions/actionTypes'
import db from '../firebaseConfig/fbConfig'
const getmenuitem = (data) => ({
    type:types.GET_MENU_ITEM,
    payload:data
})
const getmenuerror = (error) =>({
    type:types.GET_MENU_ERROR,
    payload:error
})

export const getmenuInitiate = (menu) =>{
    return function(dispatch){
        db.collection('leftsidebar').onSnapshot((querySnapshot) => {
            const menu = []
            querySnapshot.forEach((doc)=>{
                menu.push({
                    ...doc.data(),id:doc.id
                })
                dispatch(getmenuitem(menu))
            })
            }
        )
    }
}
export const getitemsInitiate = (websites) =>{
    return function(dispatch){
        db.collection('websites').onSnapshot((querySnapshot)=>{
            const websites = []
            querySnapshot.forEach((doc)=>{
                websites.push({
                    ...doc.data(),id:doc.id
                })
                dispatch(getitems(websites))
            })
        })
    }
}
