import * as types from './actionTypes'
import db from '../firebaseConfig/fbConfig';

const additem = () =>({
    type:types.ADDITEM
})

export const additemInitiate = (item) => {
    return function(dispatch){
        db.collection('websites').doc().set(item)
        dispatch(additem())
    }
}
